    private void jbtn_buscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtn_buscarActionPerformed
        // TODO add your handling code here:
        String rut;
        String nombre;
        String isapre;
        int edad;
        int celular;
        String genero;
        
        
        RegistroPaciente p = new RegistroPaciente();
        DefaultTableModel modelo = (DefaultTableModel) this.jt_Paciente.getModel();
        
        modelo.setRowCount(0);
        
        List<Paciente> lista = p.ListarP(this.jtxt_rut.getText());
        
        for (Paciente paciente : lista) {
            rut = paciente .getRut();
            nombre = paciente .getNombre();
            genero = paciente .getGenero();            
            isapre = paciente.getIsapre();
            edad = paciente.getEdad();
            celular = paciente.getCelular();
            
            
            
            modelo.addRow(new Object[]{rut,nombre,genero,isapre,edad,celular });
            
        }
    }//GEN-LAST:event_jbtn_buscarActionPerformed

